import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { setToken, setUser } from '../utils/auth';

const API = process.env.REACT_APP_API || 'http://localhost:5000';

function validEmail(e){ return /^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(e); }

export default function Login(){
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [msg,setMsg]=useState('');
  const nav = useNavigate();

  async function submit(e){
    e.preventDefault();
    setMsg('');
    if(!validEmail(email)){ setMsg('Please enter a valid email address'); return; }
    if(password.length < 6){ setMsg('Password must be at least 6 characters'); return; }
    try{
      const res = await axios.post(API + '/api/auth/login', { email, password });
      setToken(res.data.token);
      setUser(res.data.user);
      nav('/');
    }catch(err){
      setMsg(err.response?.data?.message || 'Login failed');
    }
  }

  return (
    <div className="container" style={{maxWidth:480, marginTop:80}}>
      <div className="card shadow">
        <div className="card-body p-4">
          <h3 className="card-title mb-3">Login</h3>
          {msg && <div className="alert alert-danger">{msg}</div>}
          <form onSubmit={submit}>
            <div className="mb-2"><input className="form-control" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} /></div>
            <div className="mb-3"><input className="form-control" type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} /></div>
            <button className="btn btn-primary w-100" type="submit">Login</button>
          </form>
          <hr/>
          <div className="text-center">No account? <a href="/register">Register</a></div>
        </div>
      </div>
    </div>
  );
}
