import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import axios from 'axios';
import { getToken } from '../utils/auth';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const API = process.env.REACT_APP_API || 'http://localhost:5000';

export default function Dashboard(){
  const [records, setRecords] = useState([]);

  useEffect(()=>{ fetchRecords(); }, []);

  async function fetchRecords(){
    try{
      const res = await axios.get(API + '/api/records', { headers: { Authorization: 'Bearer ' + getToken() } });
      setRecords(res.data);
    }catch(err){ console.error(err); }
  }

  const summary = records.reduce((acc, r)=>{
    acc.total = (acc.total||0) + 1;
    acc.sumScore = (acc.sumScore||0) + (r.score || 0);
    return acc;
  }, {});

  const chartData = [
    { name: 'Records', value: summary.total || 0 },
    { name: 'Avg Score', value: summary.total ? Math.round((summary.sumScore||0)/summary.total) : 0 }
  ];

  return (
    <Layout>
      <h3>Dashboard</h3>
      <div className="row">
        <div className="col-md-4">
          <div className="card mb-3 p-3"><h5>Total Records</h5><h2>{summary.total||0}</h2></div>
        </div>
        <div className="col-md-8">
          <div className="card p-3"><h5>Overview</h5>
            <div style={{height:250}}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={chartData}><XAxis dataKey="name"/><YAxis/><Tooltip/><Bar dataKey="value" /></BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
}
