import React from 'react';
import { Link } from 'react-router-dom';
import { getUser, logout } from '../utils/auth';

export default function Layout({ children }){
  const user = getUser();
  return (
    <div className="d-flex">
      <nav className="bg-dark text-white vh-100 p-3" style={{width:220}}>
        <h5 className="text-light">AAT System</h5>
        <ul className="nav flex-column">
          <li className="nav-item"><Link className="nav-link text-white" to="/">Dashboard</Link></li>
          <li className="nav-item"><Link className="nav-link text-white" to="/records">Records</Link></li>
          <li className="nav-item"><Link className="nav-link text-white" to="/users">Users</Link></li>
          <li className="nav-item"><Link className="nav-link text-white" to="/profile">Profile</Link></li>
        </ul>
      </nav>
      <div className="flex-grow-1">
        <header className="d-flex justify-content-between align-items-center p-3 border-bottom">
          <div><strong>Automated AAT Record System</strong></div>
          <div>
            <span className="me-3">Hi, {user?.name || 'User'}</span>
            <button className="btn btn-outline-secondary btn-sm" onClick={logout}>Logout</button>
          </div>
        </header>
        <main className="p-4">{children}</main>
      </div>
    </div>
  );
}
