import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { setToken, setUser } from '../utils/auth';

const API = process.env.REACT_APP_API || 'http://localhost:5000';

function validEmail(e){ return /^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(e); }

export default function Register(){
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [role,setRole]=useState('teacher');
  const [msg,setMsg]=useState('');
  const nav = useNavigate();

  async function submit(e){
    e.preventDefault();
    setMsg('');
    if(!name.trim()){ setMsg('Name is required'); return; }
    if(!validEmail(email)){ setMsg('Please enter a valid email'); return; }
    if(password.length < 6){ setMsg('Password must be at least 6 characters'); return; }
    try{
      const res = await axios.post(API + '/api/auth/register', { name, email, password, role });
      setToken(res.data.token);
      setUser(res.data.user);
      nav('/');
    }catch(err){
      if(err.response?.data?.errors) setMsg(err.response.data.errors.map(x=>x.msg).join(', '));
      else setMsg(err.response?.data?.message || 'Register failed');
    }
  }

  return (
    <div className="container" style={{maxWidth:600, marginTop:50}}>
      <div className="card shadow">
        <div className="card-body p-4">
          <h3 className="mb-3">Create Account</h3>
          {msg && <div className="alert alert-danger">{msg}</div>}
          <form onSubmit={submit}>
            <div className="mb-2"><input className="form-control" placeholder="Full name" value={name} onChange={e=>setName(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} /></div>
            <div className="mb-2"><input className="form-control" type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} /></div>
            <div className="mb-3">
              <select className="form-select" value={role} onChange={e=>setRole(e.target.value)}>
                <option value="teacher">Teacher</option>
                <option value="admin">Admin</option>
                <option value="student">Student</option>
              </select>
            </div>
            <button className="btn btn-success w-100" type="submit">Register</button>
          </form>
        </div>
      </div>
    </div>
  );
}
