import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import axios from 'axios';
import { getToken } from '../utils/auth';

const API = process.env.REACT_APP_API || 'http://localhost:5000';

export default function Users(){
  const [users, setUsers] = useState([]);

  useEffect(()=>{ fetchUsers(); }, []);

  async function fetchUsers(){
    const res = await axios.get(API + '/api/users', { headers: { Authorization: 'Bearer ' + getToken() } });
    setUsers(res.data);
  }

  async function remove(id){
    if(!window.confirm('Delete user?')) return;
    await axios.delete(API + '/api/users/' + id, { headers: { Authorization: 'Bearer ' + getToken() } });
    fetchUsers();
  }

  return (
    <Layout>
      <h3>Users</h3>
      <div className="card p-3">
        <table className="table">
          <thead><tr><th>Name</th><th>Email</th><th>Role</th><th></th></tr></thead>
          <tbody>
            {users.map(u=>(
              <tr key={u._id}><td>{u.name}</td><td>{u.email}</td><td>{u.role}</td><td><button className="btn btn-sm btn-danger" onClick={()=>remove(u._id)}>Delete</button></td></tr>
            ))}
          </tbody>
        </table>
      </div>
    </Layout>
  );
}
