import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import axios from 'axios';
import { getToken } from '../utils/auth';

const API = process.env.REACT_APP_API || 'http://localhost:5000';

export default function Records(){
  const [records, setRecords] = useState([]);
  const [form, setForm] = useState({ studentName:'', studentId:'', assessmentTitle:'', score:'', maxScore:'', remarks:'' });

  useEffect(()=>{ fetchRecords(); }, []);

  async function fetchRecords(){
    const res = await axios.get(API + '/api/records', { headers: { Authorization: 'Bearer ' + getToken() } });
    setRecords(res.data);
  }

  async function add(e){
    e.preventDefault();
    await axios.post(API + '/api/records', form, { headers: { Authorization: 'Bearer ' + getToken() } });
    setForm({ studentName:'', studentId:'', assessmentTitle:'', score:'', maxScore:'', remarks:'' });
    fetchRecords();
  }

  async function remove(id){
    if(!window.confirm('Delete?')) return;
    await axios.delete(API + '/api/records/' + id, { headers: { Authorization: 'Bearer ' + getToken() } });
    fetchRecords();
  }

  return (
    <Layout>
      <h3>Records</h3>
      <div className="card p-3 mb-3">
        <form onSubmit={add} className="row g-2">
          <div className="col-md-3"><input className="form-control" placeholder="Student name" value={form.studentName} onChange={e=>setForm({...form, studentName:e.target.value})} /></div>
          <div className="col-md-2"><input className="form-control" placeholder="Student ID" value={form.studentId} onChange={e=>setForm({...form, studentId:e.target.value})} /></div>
          <div className="col-md-3"><input className="form-control" placeholder="Assessment title" value={form.assessmentTitle} onChange={e=>setForm({...form, assessmentTitle:e.target.value})} /></div>
          <div className="col-md-1"><input className="form-control" placeholder="Score" value={form.score} onChange={e=>setForm({...form, score:e.target.value})} /></div>
          <div className="col-md-1"><input className="form-control" placeholder="Max" value={form.maxScore} onChange={e=>setForm({...form, maxScore:e.target.value})} /></div>
          <div className="col-md-2"><input className="form-control" placeholder="Remarks" value={form.remarks} onChange={e=>setForm({...form, remarks:e.target.value})} /></div>
          <div className="col-12"><button className="btn btn-primary mt-2">Add Record</button></div>
        </form>
      </div>
      <div className="card p-3">
        <table className="table">
          <thead><tr><th>Student</th><th>ID</th><th>Assessment</th><th>Score</th><th>Max</th><th>By</th><th></th></tr></thead>
          <tbody>
            {records.map(r=>(
              <tr key={r._id}>
                <td>{r.studentName}</td>
                <td>{r.studentId}</td>
                <td>{r.assessmentTitle}</td>
                <td>{r.score}</td>
                <td>{r.maxScore}</td>
                <td>{r.submittedBy?.name}</td>
                <td><button className="btn btn-sm btn-danger" onClick={()=>remove(r._id)}>Delete</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </Layout>
  );
}
